,[600901]={["id"]=600901,["infos"]={{["type"]=1,["name"]="游戏开始",["openLv"]=1,["audio"]=60090101,["script"]="CrossCore。",["index"]=1}
,{["type"]=2,["name"]="获得",["openLv"]=1,["audio"]=60090102,["script"]="代号卡提那，虽然没有什么特别的能力，但我会努力担负起这把剑的使命，竭尽所能地完成您的指令。",["index"]=2}
,{["type"]=3,["name"]="出击",["openLv"]=1,["audio"]=60090103,["script"]="装备状况确认好了，出……出发。",["index"]=3}
,{["type"]=4,["name"]="行动开始",["openLv"]=1,["audio"]=60090104,["script"]="我该怎么做，请……请指示。",["index"]=4}
,{["index"]=5,["openLv"]=1,["audio"]=60090105,["script"]="吼。",["name"]="攻击1"}
,{["index"]=6,["openLv"]=1,["audio"]=60090106,["script"]="哈。",["name"]="攻击2"}
,{["index"]=7,["openLv"]=1,["audio"]=60090107,["script"]="喝。",["name"]="攻击3"}
,{["index"]=8,["openLv"]=1,["audio"]=60090108,["script"]="哼。",["name"]="攻击4"}
,{["index"]=9,["openLv"]=1,["audio"]=60090109,["script"]="了……了解！",["name"]="技能1"}
,{["index"]=10,["openLv"]=1,["audio"]=60090110,["script"]="别担心，我这就来支援你们。",["name"]="技能2"}
,{["index"]=11,["openLv"]=1,["audio"]=60090111,["script"]="请展露出你的锋芒！",["name"]="必杀（发动时）"}
,{["index"]=12,["openLv"]=1,["audio"]=60090112,["script"]="不曾开刃的慈悲之剑·卡提那！",["name"]="必杀（结束前）"}
,{["index"]=13,["openLv"]=1,["audio"]=60090113,["script"]="乌波——（Bo二声，长音）",["name"]="被动技能1"}
,{["index"]=14,["openLv"]=1,["audio"]=60090114,["script"]="振作一下。",["name"]="被动技能2"}
,{["index"]=15,["openLv"]=1,["audio"]=60090115,["script"]="就是你！",["name"]="被动技能3"}
,{["index"]=16,["openLv"]=1,["audio"]=60090116,["script"]="Overload！",["name"]="overload"}
,{["index"]=17,["openLv"]=1,["audio"]=60090117,["script"]="呀……",["name"]="受击1"}
