,[702501]={["id"]=702501,["infos"]={{["type"]=1,["name"]="游戏开始",["openLv"]=1,["audio"]=70250101,["script"]="CrossCore。",["index"]=1}
,{["type"]=2,["name"]="获得",["openLv"]=1,["audio"]=70250102,["script"]="咿啊~睡得真好，地球人的床铺还真舒服……啊哈？你问我为什么在这里？那当然是为了从布伦希尔德那敲掉工作...说错了，当然是为了来帮助你们的啊，嗯。（前面那个“咿啊”，是伸完懒腰舒服的声音。）",["index"]=2}
,{["type"]=3,["name"]="出击",["openLv"]=1,["audio"]=70250103,["script"]="为了不耽误休息，快点结束战斗吧。",["index"]=3}
,{["type"]=4,["name"]="行动开始",["openLv"]=1,["audio"]=70250104,["script"]="敌人要是能自己暴毙就好了呢~",["index"]=4}
