,[20300]={["key"]=20300,["storyType"]=5,["name"]="袅韵个人活动1",["beginId"]=2030001,["id"]=20300,["type"]=1,["section_name"]="开端",["desc"]="袅韵与神秘的伙伴进行了雨夜密谈。"}
,[21403]={["key"]=21403,["storyType"]=16,["name"]="冬日速递2",["beginId"]=2140301,["id"]=21403,["type"]=1,["section_name"]="护送任务（上）",["desc"]="卡蒂雅需要你护卫梅尼的同时调查神秘造物，你还邀请赫格尼一起执行这个任务。赫格尼接受之后，突然出现的洛基表示自己也想参加。"}
,[10206]={["key"]=10206,["storyType"]=1,["name"]="第二章-第3节",["beginId"]=1020601,["id"]=10206,["type"]=1,["section_name"]="坠落地",["desc"]="总队长与普罗米修斯迫降在板块上，在那里等着他们的是成群的造物。"}
