,[20428]={["key"]=20428,["storyType"]=6,["name"]="糖果活动剧情29",["beginId"]=2042801,["id"]=20428,["type"]=1,["section_name"]="结局未知（下）",["desc"]="伴随着索尔达森的倒下，投影散去，之见破败的前哨基地。"}
,[10238]={["key"]=10238,["storyType"]=1,["name"]="第二章-第20节",["beginId"]=1023801,["id"]=10238,["type"]=1,["section_name"]="破局人",["desc"]="自知不敌的格恩达尔决心与布伦希尔德玉石俱焚，赶来的女武神部队阻止了两人的战斗。"}
,[10246]={["key"]=10246,["storyType"]=1,["name"]="第二章-第25节",["beginId"]=1024601,["id"]=10246,["type"]=1,["section_name"]="殊死战",["desc"]="人类与造物的大战一触即发。"}
,[10254]={["key"]=10254,["storyType"]=1,["name"]="第二章-第33节",["beginId"]=1025401,["id"]=10254,["type"]=1,["section_name"]="电子跃迁",["desc"]="来历不明的人物阻挠了探索队的行动。世界树外，洛基和丝卡蒂的战斗被强制中断。"}
,[20508]={["key"]=20508,["storyType"]=7,["name"]="夏日海韵活动剧情7",["beginId"]=2050801,["id"]=20508,["type"]=1,["section_name"]="钓客清话（上）",["desc"]="在资深钓客枪棘的指导下，一心苦恼给新故事取材的布尔特根钓上了一个意料之外的“大家伙”。"}
,[20604]={["key"]=20604,["storyType"]=8,["name"]="幻梦逸旅活动剧情5",["beginId"]=2060401,["id"]=20604,["type"]=1,["section_name"]="门里，门外（上）",["desc"]="离开地下空间的路上，C.C.C.提议干脆拆墙离开，总队长表示不需要，自己提前备份了厂房的设计布局图，知道该怎么走。走到某条过道时温蒂听到求救声，众人发现求救的是温蒂失踪的朋友薇露卡。"}
,[10318]={["key"]=10318,["storyType"]=1,["name"]="第三章-第14节",["beginId"]=1031801,["id"]=10318,["type"]=1,["section_name"]="遗民之争（下）",["desc"]="被总队长和刃齿等人击败的穆特不愿接受失败，召出了能不断吸收其他残片和核心碎片的融合造物。在这个时候，信风和闪锌正好赶到。"}
,[10326]={["key"]=10326,["storyType"]=1,["name"]="第三章-第21节",["beginId"]=1032601,["id"]=10326,["type"]=1,["section_name"]="一触即发（下）",["desc"]="总队长等人被失控的造物包围，而且还有更多造物正在赶来。为了拦阻越来越多的失控造物，闪锌和信风设下了多种拦阻。"}
,[10334]={["key"]=10334,["storyType"]=1,["name"]="第三章-第28节",["beginId"]=1033401,["id"]=10334,["type"]=1,["section_name"]="不按常理（下）",["desc"]="按照奥西里斯所说的通路，众人来到尼克罗的轨道电梯前。普罗米修斯判断轨道电梯与「冥界」系统、收束塔都有着紧密联系，一旦破坏其中一个，都会导致不可挽回的后果。"}
,[10342]={["key"]=10342,["storyType"]=1,["name"]="第三章-第36节",["beginId"]=1034201,["id"]=10342,["type"]=1,["section_name"]="高空翱翔（下）",["desc"]="暂时清除了失控造物后，总队长和普罗米修斯再次提起异变，玛哈特好奇询问总队长来到尼克罗的原因，总队长告知她，自己既然是探索队的队长，就有了相应要背负的责任。"}
,[10350]={["key"]=10350,["storyType"]=1,["name"]="第三章-第44节",["beginId"]=1035001,["id"]=10350,["type"]=1,["section_name"]="最终护卫（上）",["desc"]="进入收束塔后，刃齿询问总队长之前的经历，感叹没能和碎星人好好打上几场，活动得不够彻底。总队长表示之后会让第三分队和之前救回的分队前往其他板块搜寻队员，不会让刃齿感到无聊。"}
,[20700]={["key"]=20700,["storyType"]=9,["name"]="奇迹之夜活动剧情1",["beginId"]=2070001,["id"]=20700,["type"]=1,["section_name"]="归乡骑士",["desc"]="神秘的NOVA能量导致卡洲异变事件频发，总队长收到咎瓦尤斯邀请，利用假期返回地球调查此事，尼克罗的使者拉也一道随行。"}
,[20716]={["key"]=20716,["storyType"]=9,["name"]="奇迹之夜活动剧情16",["beginId"]=2071601,["id"]=20716,["type"]=1,["section_name"]="幕前插曲",["desc"]="世界即舞台，舞台即战场，在最终的谢幕到来之前，请接收如雷的掌声，尽情展现最无畏的英姿吧。"}
,[10406]={["key"]=10406,["storyType"]=1,["name"]="第四章-第5节",["beginId"]=1040601,["id"]=10406,["type"]=1,["section_name"]="清理工序（下）",["desc"]="希恩离开后，鸣刃对总队长的战力颇感兴趣，想要和总队长交手。总队长无奈答应，鸣刃认为用合适的武器与总队长对战才能表示对这场战斗的尊重，带领总队长返回自己的住所，寻找备用武器。"}
,[10414]={["key"]=10414,["storyType"]=1,["name"]="第四章-第13节",["beginId"]=1041401,["id"]=10414,["type"]=1,["section_name"]="无形之手（上）",["desc"]="旋律被过往所缠绕，深陷于噩梦中。第五分队其他人为旋律的情况忧心，劝慰旋律。总队长这边，总队长抛出己方对“研者”的相关资料，以此换取天阶原收束塔的资料。"}
,[10422]={["key"]=10422,["storyType"]=1,["name"]="第四章-第20节",["beginId"]=1042201,["id"]=10422,["type"]=1,["section_name"]="联结实验（上）",["desc"]="熔铄寻找鸣刃途中遇到大批感染造物，发现所有通讯频道都静默了，对此极为惊诧，想要赶往居民区开启防御系统。总队长与鸣刃见到岁稔后却发现岁稔重拾了“研者”的研究。"}
,[10430]={["key"]=10430,["storyType"]=1,["name"]="第四章-第27节",["beginId"]=1043001,["id"]=10430,["type"]=1,["section_name"]="异变之始（下）",["desc"]="顺利关闭管道阀门后，鸣刃和总队长正准备前去收束塔，却遇到了异变后的熔铄。"}
,[10438]={["key"]=10438,["storyType"]=1,["name"]="第四章-第34节",["beginId"]=1043801,["id"]=10438,["type"]=1,["section_name"]="隐藏真相（上）",["desc"]="总队长与鸣刃在收束塔下遇到了等候的赤夕，异变后的赤夕要求两人与自己战斗，记录下关于异变者战斗时的数据。"}
,[10446]={["key"]=10446,["storyType"]=1,["name"]="第四章-第42节",["beginId"]=1044601,["id"]=10446,["type"]=1,["section_name"]="未来绘景（上）",["desc"]="进入核心天使所在的收束塔区域，看到的却是超出想象的奇异机械造物。在与该机械生命体交谈后，机械生命体自称是“系尔”，借助这个机械造物的躯体与总队长交流。在交谈过后，“系尔”与总队长达成合作。"}
,[20908]={["key"]=20908,["storyType"]=11,["name"]="云端行迹活动剧情9",["beginId"]=2090801,["id"]=20908,["type"]=1,["section_name"]="塔中记载（上）",["desc"]="总队长与朝晖在双衍之塔中找到了失散的闪电，闪电告诉总队长自己受白鸟的请托，帮忙与双衍之塔的意识幻觉对战，让负责修复程序的涅毒不至于遭遇危险。"}
,[10510]={["key"]=10510,["storyType"]=1,["name"]="第五章-第8节",["beginId"]=1051001,["id"]=10510,["type"]=1,["section_name"]="长夜不宁（上）",["desc"]="审计司司长塞莱斯汀临时受命带走总队长并组建危机干预小组，在等候其他成员赶到时，维罗妮卡告知只有昆仑的新武装可以打破稽查者的防御，但蓄能时间极长，需要尽量拖延时间。"}
,[10518]={["key"]=10518,["storyType"]=1,["name"]="第五章-第14节",["beginId"]=1051801,["id"]=10518,["type"]=1,["section_name"]="基地外围（上）",["desc"]="总队长挑衅稽查者，稽查者暴怒下召唤了附近的机械造物。"}
,[10526]={["key"]=10526,["storyType"]=1,["name"]="第五章-第22节",["beginId"]=1052601,["id"]=10526,["type"]=1,["section_name"]="引导行动（上）",["desc"]="桑格维斯帮的成员找到了负责重建工程的建筑商夏洛特，从她那获得了卡洲及维洲城镇的重建图纸，还从疗养院找到了曾经担任阿卡斯助手的布蕾雅女士的日记。"}
,[10534]={["key"]=10534,["storyType"]=1,["name"]="第五章-第30节",["beginId"]=1053401,["id"]=10534,["type"]=1,["section_name"]="暗蓝之愿（下）",["desc"]="伊德海拉发现总队长及巨浪脱离监控后大怒，向沧发难。沧与伊德海拉争执，霍华德从中调停。"}
,[10542]={["key"]=10542,["storyType"]=1,["name"]="第五章-第38节",["beginId"]=1054201,["id"]=10542,["type"]=1,["section_name"]="渐见前路",["desc"]="洛贝拉用讥讽的话语刺激产生自我怀疑的昆仑，实则是借用总队长的话来开导昆仑。从海底探查后回归的巨浪似乎遭遇了某种事情，记忆逐渐消散。"}
,[21100]={["key"]=21100,["storyType"]=13,["name"]="虚海奇谭活动剧情1",["beginId"]=2110001,["id"]=21100,["type"]=1,["section_name"]="海滨邂逅",["desc"]="总队长掉入数据海，感觉自己的意识正在远去。危急关头，名为“提泽纳”的女性将总队长从数据海救起，并且一同找到失散的哈托莉。"}
,[20109]={["key"]=20109,["storyType"]=2,["name"]="卡提那个人活动第10节",["beginId"]=2010901,["id"]=20109,["type"]=1,["section_name"]="逃离",["desc"]="从宙斯的威压下逃离的奥巴瑟一言不发，只想前往总队长消失的地方，寻找总队长的踪迹。"}
,[10606]={["key"]=10606,["storyType"]=1,["name"]="第六章-第3节",["beginId"]=1060601,["id"]=10606,["type"]=1,["section_name"]="雪域荒城（下）",["desc"]="神秘武装少女自称“阿莱西娅”，并说与总队长有着深仇。总队长想从她那获取关于阿卡斯及“索菲亚之瞳”的情报，此时阿莱西娅的同伴出现。"}
,[10614]={["key"]=10614,["storyType"]=1,["name"]="第六章-第8节",["beginId"]=1061401,["id"]=10614,["type"]=1,["section_name"]="苔原烛光（上）",["desc"]="希恩离开据点后意外遇到难民们的管理者斯维特兰娜，并跟着她去到了维利卡聚居点。而总队长这边与洛贝拉交流北境战区的情况时，发现了意外的线索。"}
,[10622]={["key"]=10622,["storyType"]=1,["name"]="第六章-第14节",["beginId"]=1062201,["id"]=10622,["type"]=1,["section_name"]="真实身份（下）",["desc"]="击败了来袭的造物后，翼蓑将泰坦级造物核心交给总队长，告诉总队长可以使用高等造物的核心反向追溯来源。"}
,[10630]={["key"]=10630,["storyType"]=1,["name"]="第六章-第22节",["beginId"]=1063001,["id"]=10630,["type"]=1,["section_name"]="深入密巢（下）",["desc"]="进入兹梅伊密巢后后，总队长等人选择直接破拆至对应坐标。而在基地里，洛贝拉将控制台交给萨斯塔控制，萨斯塔表示自己担心洛贝拉的状态，洛贝拉略微柔和表示不需要担心自己。"}
,[10638]={["key"]=10638,["storyType"]=1,["name"]="第六章-第29节",["beginId"]=1063801,["id"]=10638,["type"]=1,["section_name"]="过往难追（上）",["desc"]="莫罗斯基地的人为难民们带来了资源，难民们举行了庆祝晚宴，希恩第一次真正接触了人性的温暖。在希恩离开后，在阿卡斯的蛊惑下，弥铃带领造物对聚居点进行了屠戮。"}
,[10646]={["key"]=10646,["storyType"]=1,["name"]="第六章-第33节",["beginId"]=1064601,["id"]=10646,["type"]=1,["section_name"]="深海脉动（下）",["desc"]="在击败埋伏的机械造物后，总队长再次被拉入幻觉中，看到了无比奇异的画面。在看到一个怪异阴影后，总队长被迫醒来，发现跟前的并合开关似乎就要彻底联通，于是驱使漆黑击毁了开关。"}
,[21308]={["key"]=21308,["storyType"]=15,["name"]="魔导少女活动剧情7",["beginId"]=2130801,["id"]=21308,["type"]=1,["section_name"]="深入敌营（下）",["desc"]="伊根在战斗中出现了异样，但被她搪塞过去。总队长与其沟通确认没问题后，众人再次深入造物工厂。"}
,[20301]={["key"]=20301,["storyType"]=5,["name"]="袅韵个人活动2",["beginId"]=2030101,["id"]=20301,["type"]=1,["section_name"]="不欢而散的对话",["desc"]="在一场与基因学会代表的直播对话中，袅韵被当场指控涉嫌学术造假。"}
,[21404]={["key"]=21404,["storyType"]=16,["name"]="冬日速递3",["beginId"]=2140401,["id"]=21404,["type"]=1,["section_name"]="护送任务（下）",["desc"]="众人刚来戈里刻板块，立刻就遇到了失控造物。在解决失控造物后，戈里刻的雅典娜出现在众人面前。"}
,[20413]={["key"]=20413,["storyType"]=6,["name"]="糖果活动剧情14",["beginId"]=2041301,["id"]=20413,["type"]=1,["section_name"]="无法暂停（上）",["desc"]="涡流准备前往尼约姆，却落入宇宙刑警赫尔墨斯的陷阱之中。"}
,[20429]={["key"]=20429,["storyType"]=6,["name"]="糖果活动剧情30",["beginId"]=2042901,["id"]=20429,["type"]=1,["section_name"]="幕后余声",["desc"]="这场演出过后，似乎一切都没有改变，又似乎有了改变。剧本还在手中，未来亟待书写。"}
,[20509]={["key"]=20509,["storyType"]=7,["name"]="夏日海韵活动剧情8",["beginId"]=2050901,["id"]=20509,["type"]=1,["section_name"]="钓客清话（下）",["desc"]="虽然比故事更离谱的现实经历遭到了赤溟的质疑，但枪棘的海钓兴致不减，布尔特根的苦恼也得到了总队长的开导，真是可喜可贺。"}
,[20605]={["key"]=20605,["storyType"]=8,["name"]="幻梦逸旅活动剧情6",["beginId"]=2060501,["id"]=20605,["type"]=1,["section_name"]="门里，门外（下）",["desc"]="打败围困薇露卡的造物后，C.C.C.教训薇露卡和温蒂，言语逐渐尖刻。总队长想要调停，劝解C.C.C.也是想帮助两个小孩子，为什么要用这么尖锐的言语。C.C.C.听到后想起自己的过往，瞬间愤怒，携带的无人机也开启攻击状态。"}
,[20701]={["key"]=20701,["storyType"]=9,["name"]="奇迹之夜活动剧情2",["beginId"]=2070101,["id"]=20701,["type"]=1,["section_name"]="偷渡客（上）",["desc"]="思乡心切的星椋也混进了飞行仓一同来到了地球，然而本该接应的卡洲第十三调查局却行迹诡异。"}
,[20909]={["key"]=20909,["storyType"]=11,["name"]="云端行迹活动剧情10",["beginId"]=2090901,["id"]=20909,["type"]=1,["section_name"]="塔中记载（下）",["desc"]="与AI意识生成的造物战斗后，白鸟为闪电划分的安全屋开始崩坏，总队长请朝晖帮忙，带自己和闪电离开。"}
